mapper.insert:
        INSERT INTO fs_doctor_info (
		    name
		    ,sex
		    ,age
		    ,position
		    ,major
		    ,seniority
		    ,jobNumber
		    ,phone
		    ,code
		    ,creator
		    ,lastModifier
		    ,createDate
		    ,lastModDate
		    ,status
        ) VALUES (
		    	#{name}
		    	,#{sex}
		    	,#{age}
		    	,#{position}
		    	,#{major}
		    	,#{seniority}
		    	,#{jobNumber}
		    	,#{phone}
		    	,#{code}
		    	,#{creator}
		    	,#{lastModifier}
		    	,#{createDate}
		    	,#{lastModDate}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

